import axios from 'axios'

export const get = (url, params = {}) => {
  return new Promise((resolve, reject) => {
    axios.get(url, {date}, {
      baseURL: 'http://127.0.0.1:4523/m1/4822015-4476827-default',
      headers: {
        'Content-Type': 'application/json'
      }
    }).then((response) => {
      resolve(response)
    }, err => {
      reject(err)
    })
  })
}

export const post = (url, date = {}) => {
  return new Promise((resolve, reject) => {
    axios.post(url, date, {
      baseURL: 'http://127.0.0.1:4523/m1/4822015-4476827-default',
      headers: {
        'Content-Type': 'application/json'
      }
    }).then((response) => {
      resolve(response)
    }, err => {
      reject(err)
    })
  })
}
